package chap03.lecture.binary;

public class C07Assignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//대입연산자
		// - , +=, -=, *=, /=, %=
		
		int i =100;
		i+=3; // i=i+3
		System.out.println(i);
		
		i -= 9;// i가 원래 가진값에서 -9 i=i-9
		System.out.println(i);
		
		i*=2; // i = i*2
		System.out.println(i);
		
		i /=3; // i = i/3
		System.out.println(i);
		
		i%=3; // i = i%3
		System.out.println(i);
	}

}
